sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here
    function toNum(v) {
        if (v === null || v === undefined) return 0;
        var s = ('' + v).replace(/[^0-9.\-]/g, '');
        var n = parseFloat(s);
        return isNaN(n) ? 0 : n;
    }

    function isYes(val) {
        if (val === true || val === 'true' || val === 1 || val === '1') return true;
        var s = ('' + val).toLowerCase();
        return (s === 'yes' || s === 'y' || s === 'true');
    }

    //Numeric fields
    var api = toNum(current.u_string_7); //Asset Priority Ranking
    var rf = toNum(current.risk_score); //Ranking Factor Score

    //Bonuses
    var sowScore = isYes(current.has_a_statement_of_work_sow_been_developed_for_this_project) ? 25 : 0;

    var engScore =
        isYes(current.has_an_engineered_cost_estimate_developed_for_this_project) ||
        isYes(current.has_an_engineered_cost_estimate_developed_for_this_project) ?
        25 :
        0;


    var energyScore = 0;
    if (isYes(current.energy_efficiency)) {

        if ((current.time_for_roi + '') === '< 5 years') {
            energyScore = 50;

        }
    }

    //Final Total
    current.total_project_score =
        (api * .35) +
        (rf * .65) +
        sowScore +
        engScore +
        energyScore;

})(current, previous);
