sys_script.script:
(function executeRule(current, previous) {

    //Generate if empty
    if (!current.u_project_id.nil())
        return;

    //FMC should exist
    if (current.u_fmc.nil())
        return;

    var fmc = current.getDisplayValue('u_fmc').toString().trim();

    var year = new GlideDateTime()
        .getYearLocalTime()
        .toString()
        .slice(-2);

    var key = fmc + '-' + year;

    var next = getNextNumberScoped(key, fmc);

    //Pad sequence -001
    var seq = next.toString().padStart(3, '0');

    //Build ProjectID
    current.u_project_id = fmc + '-' + year + '-' + seq;

    //-------------------------------------------------------------------------------------------
    function getNextNumberScoped(key, fmc) {

        var TABLE = 'x_1831875_amp2_0_project_id_generator';

        for (var i = 0; i < 5; i++) {
            var gen = new GlideRecord(TABLE);
            gen.addQuery('key', key);
            gen.setLimit(1);
            gen.query();

            if (gen.next()) {
                var last = parseInt(gen.u_last_number, 10) || 0;
                gen.u_last_number = last + 1;
                gen.update();
                return gen.u_last_number;
            }
            try {
                gen.initialize();
                gen.key = key;
                gen.u_fmc = fmc;
                gen.u_last_number = 1;
                gen.insert();
                return 1;
            } catch (e) {
                //if key duplicated, loop again and re-query

            }

            //if fail, return 1;
            return 1;

        }

        function pad(num, size) {
            var s = num.toString();
            while (s.length < size) s = '0' + s;
            return s;
        }
    }
})(current, previous);
