sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    var isYes = (newValue === 'yes' || newValue === 'true' || newValue === '1');

    //SOW field to anchor annotation
    var anchorField = 'has_a_statement_of_work_sow_been_developed_for_this_project';

    //Clear any existing message first(prevents dupes)
    g_form.hideFieldMsg(anchorField, true);

    if (isYes) {
        g_form.showFieldMsg(
            anchorField,
            'Please attach Statement of Work developed for this project.', 'info', true
        );
    }
}
