sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') return;

    // Get the floor record
    g_form.getReference('floor', function(floorRec) {
        if (!floorRec) return;

        // Set building from floor
        if (floorRec.building) {
            g_form.setValue('building', floorRec.building);

            // Get the building record to set facility
            g_form.getReference('building', function(buildingRec) {
                if (buildingRec && buildingRec.facility) {
                    g_form.setValue('facility', buildingRec.facility);
                }
            });
        }
    });
}
