sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') return;

    // Get the facility record
    g_form.getReference('facility', function(facilityRec) {
        if (!facilityRec) return;

        // Set complex from facility
        if (facilityRec.complex) {
            g_form.setValue('complex', facilityRec.complex);

            // Get the complex record to set campus
            g_form.getReference('complex', function(complexRec) {
                if (complexRec && complexRec.campus) {
                    g_form.setValue('campus', complexRec.campus);
                }
            });
        }
    });
}
