sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  if (isLoading || isTemplate) return;

  function toNumber(fieldName) {
    var v = g_form.getValue(fieldName);

    // Treat blank/null as 0
    if (!v) return 0;

    // Remove commas, currency symbols, spaces, etc. (keep digits, dot, minus)
    v = ('' + v).replace(/[^0-9.\-]/g, '');

    var n = parseInt(v);
    return isNaN(n) ? 0 : n;
  }

  var fmc   = toNumber('u_fmc_funds');
  var hq    = toNumber('u_hq_funds');
  var other = toNumber('u_other_funds');

  var total = fmc + hq + other;

  g_form.setValue('u_total_funds', total); }
