sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading) {
    if (isLoading || newValue === '') return;

    var ga = new GlideAjax('SpaceUtils');
    ga.addParam('sysparm_name', 'getBuildingAndFacility');
    ga.addParam('sysparm_floor', newValue);
    ga.getXMLAnswer(function(response) {
        if (response) {
            var data = JSON.parse(response);
            if (data.building) g_form.setValue('building', data.building);
            if (data.facility) g_form.setValue('facility', data.facility);
        }
    });
}
