sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    //Only run if Project ID exists
    if (current.u_project_id.nil())
        return;

    var pid = (current.u_project_id + '');
    var parts = pid.split('-');
    if (parts.length < 3)
        return;

    var fmc = (parts[0] || '').trim();
    var year = (parts[1] || '').trim();
    if (!fmc || !year)
        return;

    var key = fmc + '-' + year; //AKC-26

    var PROJECT_TABLE = 'x_1831875_amp2_0_project_package';
    var GEN_TABLE = 'x_1831875_amp2_0_project_id_generator';

    //Find the highest remaining Project ID for this key excluding record being deleted
    var maxSeq = 0;
    var gr = new GlideRecord(PROJECT_TABLE);
    gr.addQuery('u_project_id', 'STARTSWITH', key + '-');
    gr.addQuery('sys_id', '!=', current.sys_id);
    gr.orderByDesc('u_project_id'); //works bc seq is zero-padded
    gr.setLimit(1);
    gr.query();

    if (gr.next()) {
        //Parse the last segment
        var remainingPid = (gr.u_project_id + '').trim();
        var p2 = remainingPid.split('-');
        if (p2.length >= 3) {
            var seqStr = (p2[2] || '').trim();
            var n = parseInt(seqStr, 10);
            if (!isNaN(n))
                maxSeq = n;

        }


    }
    var gen = new GlideRecord(GEN_TABLE);
    gen.addQuery('key', key);
    gen.setLimit(1);
    gen.query();

    if (gen.next()) {
        if (maxSeq <= 0) {
            //no remaining records for this key so reset to allow seq startover
            gen.deleteRecord();
        } else {
            gen.u_last_number = maxSeq;
            gen.update();
        }
    }
})(current, previous);
