sys_script_fix.script:
(function() {
    var updated = 0;
    var skipped = 0;

    var gr = new GlideRecord('x_1831875_amp2_0_property_table');
    gr.query();

    while (gr.next()) {
        var common = (gr.getValue('prop_common_name') || '').toString().trim();
        var official = (gr.getValue('u_property_name') || '').toString().trim();

        var label = '';
        if (common && official && common !== official) {
            label = common + '(' + official + ')';

        } else if (common) {
            label = common;
        } else {
            label = official;
        }

        //Only uodate if changed (reduces DB writes)
        if ((gr.getValue('u_display_label') || '') !== label) {
            gr.setValue('u_display_label', label);
            gr.update();
            updated++;
        } else {
            skipped++;
        }
    }
    gs.print('Property Display Label backfill complete. Updated=' + updated + ', Skipped=' + skipped);
})();
