sys_script_fix.script:
(function() {
        var COMPLEX_TBL = 'x_1831875_amp2_0_x_amp2_fmc_complex';
        var FMC_TBL = 'x_1831875_amp2_0_fmc_lookup_table';

        var updated = 0,
            skippedBlank = 0,
            skippedBad = 0;

        var c = new GlideRecord(COMPLEX_TBL);
        c.query();
        while (c.next()) {
            //only fill if new fmc field is empty
            if (!c.u_fmc_ref.nil()) continue;

            var fmcId = (c.u_fmc || '').toString().trim();
                if (!fmcId) {
                    skippedBlank++;
                    continue;
                }

                //validate sys_id exists in FMC table
                var f = new GlideRecord(FMC_TBL);
                if (!f.get(fmcId)) {
                    skippedBad++;
                    continue;
                }

                c.u_fmc_ref = fmcId; c.update(); updated++;

            }

            gs.print('Backfill complete. Updated=' + updated +
                ', SkippedBlank=' + skippedBlank +
                ', SkippedBadSysId=' + skippedBad);


        })();
