sys_script_include.script:
var AutoPopulateUtils = Class.create();
AutoPopulateUtils.prototype = {
    /**
     * Populates fields on the current record by walking a relationship chain.
     * @param {GlideRecord} current - The current record in the BR
     * @param {string} triggerField - Field name that triggers the update
     * @param {Array} chain - Array of { field: 'ref_field', alias: 'target_field' }
     */
    populateFromChain: function(current, triggerField, chain) {
        if (current[triggerField].changes()) {
            var startRec = current[triggerField].getRefRecord();
            if (startRec.isValidRecord()) {
                var currentRec = startRec;
                chain.forEach(function(step) {
                    if (currentRec.isValidRecord() && currentRec[step.field]) {
                        current[step.alias] = currentRec[step.field];
                        currentRec = currentRec[step.field].getRefRecord();
                    }
                });
            }
        }
    },

    type: 'AutoPopulateUtils'
};
