sys_script_include.script:
var SpaceUtils = Class.create();
SpaceUtils.prototype = Object.extendsObject(AbstractAjaxProcessor, {

    getBuildingAndFacility: function() {
        var floorId = this.getParameter('sysparm_floor');
        var result = {};

        if (floorId) {
            var floorRec = new GlideRecordSecure('floor'); // Replace with your floor table
            if (floorRec.get(floorId)) {
                result.building = floorRec.building.toString();

                if (floorRec.building) {
                    var buildingRec = floorRec.building.getRefRecord();
                    if (buildingRec.isValidRecord()) {
                        result.facility = buildingRec.facility.toString();
                    }
                }
            }
        }
        return JSON.stringify(result);
    }

});
